import React, { useEffect, useState } from "react";
import axios from "axios";
import ReactAnimatedWeather from "react-animated-weather";

function Forecast({ weather }) {
  const { data } = weather;
  const [forecastData, setForecastData] = useState([]);
  const [isCelsius, setIsCelsius] = useState(true); // Track temperature unit

  useEffect(() => {
    const fetchForecastData = async () => {
      const apiKey = "dfef1c685e2f3beb7af9cc078f47db1c";
      const url = `https://api.openweathermap.org/data/2.5/forecast?q=${data.name}&appid=${apiKey}&units=metric`;

      try {
        const response = await axios.get(url);
        const dailyForecast = processForecastData(response.data.list);
        setForecastData(dailyForecast);
      } catch (error) {
        console.log("Error fetching forecast data:", error);
      }
    };

    if (data.name) {
      fetchForecastData();
    }
  }, [data.name]);

  // Process the data to get daily forecasts
  const processForecastData = (list) => {
    const dailyData = [];
    const map = new Map();

    list.forEach((entry) => {
      const date = new Date(entry.dt_txt).toLocaleDateString("en-US", {
        weekday: "long",
        month: "long",
        day: "numeric",
      });

      if (!map.has(date)) {
        map.set(date, {
          date,
          temp_min: entry.main.temp_min,
          temp_max: entry.main.temp_max,
          icon: entry.weather[0]?.icon,
          description: entry.weather[0]?.description,
          dt: entry.dt, // Add timestamp to determine day or night
        });
      } else {
        const existing = map.get(date);
        map.set(date, {
          ...existing,
          temp_min: Math.min(existing.temp_min, entry.main.temp_min),
          temp_max: Math.max(existing.temp_max, entry.main.temp_max),
        });
      }
    });

    map.forEach((value) => dailyData.push(value));
    return dailyData.slice(0, 5); // Get only 5 days
  };

  const formatDay = (dateString) => {
    const options = { weekday: "short" };
    const date = new Date(dateString);
    return date.toLocaleDateString("en-US", options);
  };

  const toggleTemperatureUnit = () => {
    setIsCelsius((prevState) => !prevState);
  };

  const convertToFahrenheit = (temperature) => {
    return Math.round((temperature * 9) / 5 + 32);
  };

  const renderTemperature = (temperature) => {
    if (isCelsius) {
      return Math.round(temperature);
    } else {
      return convertToFahrenheit(temperature);
    }
  };

  // Determine whether it is day or night based on the timestamp
  const isDayTime = (timestamp) => {
    const hour = new Date(timestamp * 1000).getHours();
    return hour >= 6 && hour < 18; // Consider day time from 6 AM to 6 PM
  };

  // Function to determine the correct animation based on the weather description and time of day
  const getWeatherAnimation = (description, timestamp) => {
    const lowerCaseDescription = description.toLowerCase();
    const dayTime = isDayTime(timestamp);

    if (lowerCaseDescription.includes("rain")) {
      return "RAIN";
    } else if (lowerCaseDescription.includes("cloud")) {
      return dayTime ? "CLOUDY" : "CLOUDY_NIGHT";
    } else if (lowerCaseDescription.includes("sun") && dayTime) {
      return "CLEAR_DAY";
    } else if (lowerCaseDescription.includes("sun") && !dayTime) {
      return "CLEAR_NIGHT";
    } else if (lowerCaseDescription.includes("moon") || !dayTime) {
      return "CLEAR_NIGHT";
    } else if (lowerCaseDescription.includes("snow")) {
      return "SNOW";
    } else {
      return dayTime ? "PARTLY_CLOUDY_DAY" : "PARTLY_CLOUDY_NIGHT"; // Default animation
    }
  };

  return (
    <div>
      <div className="city-name">
        <h2>
          {data.name}, <span>{data.sys?.country}</span>
        </h2>
      </div>
      <div className="date">
        <span>{new Date().toLocaleDateString()}</span>
      </div>
      <div className="temp">
        {data.weather && data.weather[0]?.icon && (
          <ReactAnimatedWeather
            icon={getWeatherAnimation(data.weather[0]?.description, data.dt)}
            color="goldenrod"
            size={64}
            animate={true}
          />
        )}
        {renderTemperature(data.main?.temp)}
        <sup className="temp-deg" onClick={toggleTemperatureUnit}>
          {isCelsius ? "째C" : "째F"} | {isCelsius ? "째F" : "째C"}
        </sup>
      </div>
      <p className="weather-des">
        {data.weather ? data.weather[0]?.description : "No description available"}
      </p>
      <div className="weather-info">
        <div className="col">
          <ReactAnimatedWeather icon="WIND" size="40" />
          <div>
            <p className="wind">{data.wind?.speed} m/s</p>
            <p>Wind speed</p>
          </div>
        </div>
        <div className="col">
          <ReactAnimatedWeather icon="RAIN" size="40" />
          <div>
            <p className="humidity">{data.main?.humidity}%</p>
            <p>Humidity</p>
          </div>
        </div>
      </div>
      <div className="forecast">
        <h3>5-Day Forecast:</h3>
        <div className="forecast-container">
          {forecastData.map((day, index) => (
            <div className="day" key={index}>
              <p className="day-name">{formatDay(day.date)}</p>
              <ReactAnimatedWeather
                icon={getWeatherAnimation(day.description, day.dt)}
                color="goldenrod"
                size={48}
                animate={true}
              />
              <p className="day-temperature">
                {Math.round(day.temp_min)}째/{" "}
                <span>{Math.round(day.temp_max)}째</span>
              </p>
              <p>{day.description}</p>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default Forecast;
